const { VulneraAI } = require('@vulneraai/sdk');
const express = require('express');
const fs = require('fs').promises;

const app = express();
const client = new VulneraAI({
  apiKey: process.env.VULNERAAI_API_KEY,
  baseURL: 'https://api.vulneraai.com/v1'
});

// Endpoint –¥–ª—è –∑–∞–ø—É—Å–∫–∞ —Å–∫–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è
app.post('/api/scan', async (req, res) => {
  try {
    const { targetUrl } = req.body;

    // –°–æ–∑–¥–∞–Ω–∏–µ —Å–∫–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è
    const scan = await client.scans.create({
      name: `Security Scan - ${targetUrl}`,
      targets: [{ type: 'url', value: targetUrl }],
      scanType: 'full',
      aiConfig: {
        battlenetEnabled: true,
        ragSearchEnabled: true,
        zeroDayDetection: true
      },
      notifications: {
        webhookUrl: `${process.env.WEBHOOK_BASE_URL}/webhook`,
        onCriticalFinding: true
      }
    });

    // –ó–∞–ø—É—Å–∫ –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–æ
    client.scans.start(scan.id).catch(err => {
      console.error(`Scan ${scan.id} failed:`, err);
    });

    res.json({
      success: true,
      scanId: scan.id,
      status: 'started',
      statusUrl: `/api/scan/${scan.id}/status`
    });
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// Endpoint –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è —Å—Ç–∞—Ç—É—Å–∞
app.get('/api/scan/:scanId/status', async (req, res) => {
  try {
    const { scanId } = req.params;
    const status = await client.scans.getStatus(scanId);

    res.json({
      scanId,
      status: status.status,
      progress: status.progress,
      vulnerabilitiesCount: status.vulnerabilitiesCount,
      criticalCount: status.criticalCount
    });
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// Endpoint –¥–ª—è —Å–∫–∞—á–∏–≤–∞–Ω–∏—è –æ—Ç—á–µ—Ç–∞
app.get('/api/scan/:scanId/report/:format', async (req, res) => {
  try {
    const { scanId, format } = req.params;

    const reportData = await client.scans.downloadReport(
      scanId,
      { format }
    );

    const mimeTypes = {
      pdf: 'application/pdf',
      json: 'application/json',
      xlsx: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
      sarif: 'application/json'
    };

    res.set('Content-Type', mimeTypes[format]);
    res.set('Content-Disposition', `attachment; filename="report_${scanId}.${format}"`);

    if (typeof reportData === 'string') {
      res.send(reportData);
    } else {
      res.send(Buffer.from(reportData));
    }
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// Webhook –æ–±—Ä–∞–±–æ—Ç—á–∏–∫
app.post('/webhook', express.json(), async (req, res) => {
  try {
    const signature = req.headers['x-vulneraai-signature'];
    const payload = JSON.stringify(req.body);

    // –ü—Ä–æ–≤–µ—Ä–∏—Ç—å –ø–æ–¥–ø–∏—Å—å (—Å–º. —Ä–∞–∑–¥–µ–ª webhooks)
    const isValid = verifyWebhookSignature(
      payload,
      signature,
      process.env.WEBHOOK_SECRET
    );

    if (!isValid) {
      return res.status(401).json({ error: 'Invalid signature' });
    }

    const event = req.body;

    console.log(`Webhook event: ${event.event}`);

    if (event.event === 'scan.completed') {
      await handleScanCompleted(event.data);
    } else if (event.event === 'vulnerability.critical') {
      await handleCriticalVulnerability(event.data);
    }

    res.json({ status: 'received' });
  } catch (error) {
    console.error('Webhook error:', error);
    res.status(500).json({ error: error.message });
  }
});

async function handleScanCompleted(data) {
  console.log(`Scan ${data.scan_id} completed`);
  console.log(`  Duration: ${data.duration}s`);
  console.log(`  Vulnerabilities: ${data.vulnerabilities_count}`);

  // –û—Ç–ø—Ä–∞–≤–∏—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ –≤ Slack
  await notifySlack({
    text: `‚úÖ Scan completed: ${data.scan_id}`,
    blocks: [{
      type: "section",
      text: { type: "mrkdwn", text: `*Scan Results*\nTarget: ${data.target}\nVulnerabilities: ${data.vulnerabilities_count}` }
    }]
  });
}

async function handleCriticalVulnerability(data) {
  console.log(`Critical vulnerability: ${data.vulnerability.title}`);

  // –°–æ–∑–¥–∞—Ç—å GitHub Issue
  await client.integrations.github.createIssue({
    repository: 'my-org/my-repo',
    title: `[CRITICAL] ${data.vulnerability.title}`,
    body: `CVE: ${data.vulnerability.cve_id}\nCVSS: ${data.vulnerability.cvss_score}\n${data.vulnerability.description}`,
    labels: ['security', 'critical']
  });
}

app.listen(3000, () => {
  console.log('VulneraAI Integration Server running on port 3000');
});
