const { VulneraAI } = require('@vulneraai/sdk');
const fs = require('fs');

const client = new VulneraAI({
  apiKey: process.env.VULNERAAI_API_KEY,
  baseURL: 'https://api.vulneraai.com/v1'
});

async function scanWebsite(targetUrl) {
  try {
    // –°–æ–∑–¥–∞–Ω–∏–µ –∏ –∑–∞–ø—É—Å–∫ —Å–∫–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è
    const scan = await client.scans.create({
      name: `Security Scan - ${targetUrl}`,
      targets: [{ type: 'url', value: targetUrl }],
      scanType: 'full',
      aiConfig: {
        battlenetEnabled: true,
        ragSearchEnabled: true,
        zeroDayDetection: true
      },
      notifications: {
        webhookUrl: 'https://your-app.com/webhook',
        onCriticalFinding: true
      }
    });

    console.log(`Scan created: ${scan.id}`);

    await client.scans.start(scan.id);
    console.log('Scan started...');

    // Polling —Å—Ç–∞—Ç—É—Å–∞
    let status;
    do {
      await new Promise(resolve => setTimeout(resolve, 30000)); // 30 sec
      status = await client.scans.getStatus(scan.id);
      console.log(`Scan progress: ${status.progress}%`);
    } while (status.status === 'running');

    if (status.status === 'completed') {
      // –ü–æ–ª—É—á–µ–Ω–∏–µ —É—è–∑–≤–∏–º–æ—Å—Ç–µ–π
      const vulnerabilities = await client.scans.getVulnerabilities(scan.id);

      const criticalCount = vulnerabilities.filter(v => v.severity === 'critical').length;
      const highCount = vulnerabilities.filter(v => v.severity === 'high').length;

      console.log(`Scan completed: ${criticalCount} critical, ${highCount} high severity vulnerabilities`);

      // –≠–∫—Å–ø–æ—Ä—Ç –≤ SARIF –¥–ª—è GitHub Security
      const sarifReport = await client.scans.exportReport(scan.id, { format: 'sarif' });
      fs.writeFileSync('vulnerabilities.sarif', JSON.stringify(sarifReport));

      return {
        scanId: scan.id,
        vulnerabilities,
        summary: { critical: criticalCount, high: highCount }
      };
    } else {
      throw new Error(`Scan failed: ${status.error}`);
    }
  } catch (error) {
    console.error('Scan error:', error.message);
    throw error;
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
scanWebsite('https://example.com')
  .then(result => {
    console.log('Scan results:', result.summary);
