package main

import (
    "context"
    "fmt"
    "log"
    "time"

    "github.com/vulneraai/go-sdk"
)

func main() {
    client := vulneraai.New("sk-vulneraai-your-token")

    ctx, cancel := context.WithTimeout(context.Background(), 30*time.Minute)
    defer cancel()

    // –°–æ–∑–¥–∞–Ω–∏–µ —Å–∫–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è
    scan, err := client.Scans.Create(ctx, &vulneraai.CreateScanRequest{
        Name: "Go Integration Test",
        Targets: []vulneraai.Target{
            {Type: "url", Value: "https://api.example.com"},
        },
        ScanType: "full",
        AIConfig: &vulneraai.AIConfig{
            BattleNetEnabled: true,
            RAGSearchEnabled: true,
        },
    })

    if err != nil {
        log.Fatal(err)
    }

    fmt.Printf("Created scan: %s\n", scan.ID)

    // –ó–∞–ø—É—Å–∫ —Å–∫–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è
    if err := client.Scans.Start(ctx, scan.ID); err != nil {
        log.Fatal(err)
    }

    // –ú–æ–Ω–∏—Ç–æ—Ä–∏–Ω–≥ —Å –∫–∞–Ω–∞–ª–∞–º–∏
    events := make(chan *vulneraai.Event, 10)
    go func() {
        defer close(events)
        client.Scans.StreamEvents(ctx, scan.ID, events)
    }()

    // –û–±—Ä–∞–±–æ—Ç–∫–∞ —Å–æ–±—ã—Ç–∏–π
    for event := range events {
        fmt.Printf("Event: %s\n", event.Type)

        if event.Type == "scan.completed" {
            break
        }

        if event.Type == "vulnerability.critical" {
            vuln := event.Data.Vulnerability
            fmt.Printf("Critical vulnerability found: %s (CVSS: %.1f)\n", 
                vuln.Title, vuln.CVSSScore)
        }
    }

    // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ñ–∏–Ω–∞–ª—å–Ω—ã—Ö —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
    vulnerabilities, err := client.Scans.GetVulnerabilities(ctx, scan.ID)
    if err != nil {
        log.Fatal(err)
    }

    criticalCount := 0
    for _, vuln := range vulnerabilities {
        if vuln.Severity == "critical" {
            criticalCount++
        }
    }

    fmt.Printf("Scan completed. Found %d critical vulnerabilities\n", criticalCount)
}
